<pre><code>
import { Injectable } from '@angular/core';
import { CanActivate, ActivatedRouteSnapshot, RouterStateSnapshot, Router } from '@angular/router';
import { take, map } from 'rxjs/operators';
import { AuthService } from '../services';
import { Observable } from 'rxjs';

@Injectable()
export class NoAuthGuard implements CanActivate {

  constructor(private _router: Router,
              private authService: AuthService) { }

  canActivate(_next: ActivatedRouteSnapshot, _state: RouterStateSnapshot): Observable<boolean> | Promise<boolean> | boolean {
    console.log('I m in No auth guard Service');

    this.authService.populate();

    return this.authService.isAuthenticated.pipe(take(1), map(isAuth => {
        if (isAuth) { this._router.navigate(['/404'], { skipLocationChange: true }); }
        return !isAuth;
    }));
  }
}
