import { Service } from './../../core/service-helper/service';
import { Injectable } from '@angular/core';
import { HttpService } from 'app/core/service-helper/http.service';

@Injectable()
export class ConsultaprecoService {
  private url =  Service.API_ENDPOINT;
  constructor(private httpService: HttpService){}

  productsAnp(){
    return this.httpService.get(this.url + 'produtos/anp',  null);
  }

  filterFlag(){
    return this.httpService.get(this.url + 'dash/combustivel/GetFiltroBandeira');
  }
  
  activeStates(){
    return this.httpService.get(this.url + 'estados/ativos');
  }

  getCompanyName(){
    return this.httpService.post(this.url + 'dash/combustivel/listaPostos/razaoSocial', null);
  }

  getCitiesByStateId(stateId){
    return this.httpService.get(this.url + stateId + '/cidades');
  }

  consultaPreco(Page: string, Ordem: string, Direcao: string, QtdItem: string, optional){
    let params = {
