<form class="form-horizontal" [formGroup]="EmployeeForm" (ngSubmit)="onSubmit()">
  <div class="panel panel-heading">
    <h3 class="panel-title">Create Employee</h3>
  </div>
  <div class="panel-body">
    <div class="form-group"  [ngClass]="{'has-error':formErrors.fullName}">
      <label for="fullName" class="col-sm-2 control-label"> FullName : </label>
      <div class="col-sm-8">
        <input id="fullName" type="text" class="form-control" 
        formControlName="fullName"
          (blur)="logValidationErrors()"
        >
      <span class="help-block" *ngIf="formErrors.fullName">
          
           {{formErrors.fullName}}
             
      </span>
     
      </div>
    </div>
    <div class="form-group" >
        <label for="" class="col-sm-2 control-label">Contact Preferrence</label>
        <div class="col-md-8">
         
         <label for="" class="radio-inline">
           <input type="radio" value="email"
            
           formControlName="contactPreference"
            >Email
         </label>
         <label for="" class="radio-inline">
           <input type="radio" value="phone"
            
            formControlName="contactPreference"
             >Phone
         </label>
       
        </div>
      </div>
    <div class="form-group" [ngClass]="{'has-error':formErrors.email}">
      <label for="email" class="col-sm-2 control-label">Email : </label>
      <div class="col-sm-8">
        <input id="email" type="text" class="form-control" 
        formControlName="email"
        (blur)="logValidationErrors()"
        >
      </div>
      <span class="help-block" *ngIf="formErrors.email">
        {{formErrors.email}}
      </span>
    </div> 
    <div class="form-group" [ngClass]="{'has-error':formErrors.phone}">
        <label for="phone" class="col-sm-2 control-label">Phone : </label>
        <div class="col-sm-8">
          <input id="phone" type="text" class="form-control" 
          formControlName="phone"
          (blur)="logValidationErrors()"
          >
        </div>
        <span class="help-block" *ngIf="formErrors.phone">
          {{formErrors.phone}}
        </span>
      </div> 

 
    <div class="well">
      <div formGroupName="skills">
        <div class="form-group" [ngClass]="{'has-error': formErrors.skillName}">
          <label for="" class="col-sm-2 control-label">Skill</label>
          <div class="col-sm-4">
            <input type="text" class="form-control"
              formControlName="skillName"
            >
            <span class="help-block" *ngIf="formErrors.skillName">
              {{formErrors.skillName}}
            </span>
          </div>
        </div>

        <div class="form-group" [ngClass]="{'has-error': formErrors.experienceInYears}">
            <label for="" class="col-sm-2 control-label">experienceInYears</label>
            <div class="col-sm-4">
              <input type="text" class="form-control"
                formControlName="experienceInYears"
              >
              <span class="help-block" *ngIf="formErrors.experienceInYears">
                {{formErrors.experienceInYears}}
              </span>
            </div>
          </div>

          <div class="form-group" [ngClass]="{'has-error': formErrors.proficiency}">
              <label for="" class="col-sm-2 control-label">proficiency</label>
              <div class="col-md-8">
               
               <label for="" class="radio-inline">
                 <input type="radio" value="begginer" formControlName="proficiency"
                  (blur)="logValidationErrors()">
               </label>
               <label for="" class="radio-inline">
                 <input type="radio" value="intermediate"
                  formControlName="proficiency"
                  (blur)="logValidationErrors()">
               </label>
               <label for="" class="radio-inline">
                 <input type="radio" value="advanced" 
                 formControlName="proficiency"
                 (blur)="logValidationErrors()">
               </label>
               <span class="help-block" *ngIf="formErrors.proficiency">
                  {{formErrors.proficiency}}
                </span>
              </div>
            </div>


      </div>
    </div>
  </div>

  <div class="panel-footer">
    <div class="btn-toolbar">
        <button class="btn btn-primary" type="submit">Save</button>
        <button class="btn btn-primary" type="button" (click)="onLoadDataClick()">Load Data</button>
        <button class="btn btn-primary" type="button" (click)="onclearClick()">clear Data</button>
    </div>
  

  </div> 
</form>

                   
