  ngOnInit() {
     this.EmployeeForm = this.fb.group({
      fullName:['', [Validators.required, Validators.minLength(5), Validators.maxLength(10)]],
      email:['', [Validators.required]],
      contactPreference: ['email'],
      phone: [''],
      skills: this.fb.group({
        skillName: ['', [Validators.required]],
        experienceInYears: ['', [Validators.required]],
        proficiency: ['', [Validators.required]]
      })

    });
    this.EmployeeForm.valueChanges.subscribe((data)=>{
      this.onContactPreferencechange(data);
    })
    this.EmployeeForm.valueChanges.subscribe((data)=>{
      this.logValidationErrors(this.EmployeeForm);
    })
  }
 onContactPreferencechange(selectedValue: string){
   const phoneControl = this.EmployeeForm.get('phone');
   if(selectedValue === 'phone'){
    phoneControl.setValidators([Validators.required])
   }else{
     phoneControl.clearValidators();
   }
   phoneControl.updateValueAndValidity();
 }
  getControls(group: FormGroup){
    Object.keys(group.controls).forEach((key: string)=>{
      const abstractControl = group.get(key);
      if(abstractControl instanceof FormGroup){
        this.getControls(abstractControl);
      }else{
        // console.log('keys : ', key + '  values are: ', abstractControl.value)
      }
      
    })
  }


  logValidationErrors(group: FormGroup = this.EmployeeForm){
    // Loop through each control key in the FormGroup
    Object.keys(group.controls).forEach((key : string)=>{
          // Get the control. The control can be a nested form group
      const abstractControl = group.get(key);
         // If the control is nested form group, recursively call
    // this same method
      if(abstractControl instanceof FormGroup){
        this.logValidationErrors(abstractControl);
          // If the control is a FormControl
      }else{
         // Clear the existing validation errors
         this.formErrors[key]='';
         if(abstractControl && !abstractControl.valid &&
           (abstractControl.touched || abstractControl.dirty)){
            // Get all the validation messages of the form control
        // that has failed the validation
        const messages = this.validationMessages[key];
       
         // Find which validation has failed. For example required,
        // minlength or maxlength. Store that error message in the
        // formErrors object. The UI will bind to this object to
        // display the validation errors
        for(const errorKey in abstractControl.errors){
          if(errorKey){
            this.formErrors[key]+= messages[errorKey]+' ';
           }
        }
         }
      }
    })
  }
  onLoadDataClick() {
    // this.EmployeeForm.setValue({
    //   fullName: 'raja',
    //   email: 'rajasheker@gmail.com',
    //   skills:  ({
    //     skillName:   'Angular',
    //     expInYears:  '5.5',
    //     proficiency:  'Begginer',

    //   })
    // })
    this.getControls(this.EmployeeForm);
   }
  onclearClick(){
    // this.EmployeeForm.reset()
    this.EmployeeForm.reset();
  }
  onSubmit(): void {
     
  }

}

