  @Autowired() public app: AppService;

  public title = '';

  public ngOnInit(): void {
    this.title = this.app.hello();
  }
}
```

```ts
@Injectable()
export class AppService {
  @Autowired() public my: AnyService;

  public hello(): string {
    return this.my.title;
  }
}
```

