            public string UrlPath { get; set; }
            public ICollection<UserDatabase> UserDatabases { get; set; }
            public ICollection<PracticeOccupation> PracticeOccupation { get; set; }
            public virtual ICollection<ApplicationRole> ApplicationUserRole { get; set; }
        }


    public class ApplicationRole : IdentityRole
    {
        [Required]
        public Guid DatabaseId { get; set; }
        [ForeignKey("DatabaseId")]
        public virtual PracticeDatabase Database { get; set; }
    }


