				token = new JwtSecurityTokenHandler().WriteToken(token),
				expiration = token.ValidTo
			};
		}
		private async Task<Object> CreateToken(ApplicationUser user)
		{
			var claims = await CreateUserClaims(user);
			var token = CreateToken(claims);
			return token;
		}
