using Microsoft.AspNetCore.Razor.TagHelpers;
using Microsoft.Extensions.Logging;

namespace WebApplication1
{
    [HtmlTargetElement("parent-tag", TagStructure = TagStructure.NormalOrSelfClosing)]
    public class ParentTagTagHelper : TagHelper
    {
        private readonly ILogger _logger;

        public ParentTagTagHelper(ILogger<ParentTagTagHelper> logger)
        {
            _logger = logger;
        }
        public override async Task ProcessAsync(TagHelperContext context, TagHelperOutput output)
        {
            try
            {
                (await output.GetChildContentAsync()).GetContent();
            }
            catch (Exception e)
            {
                _logger.LogError("child tag helper failed", e);
                output.SuppressOutput();
            }
        }
    }
}

