            // Get our User DbSet
            var users = base.Users.Where(u=>u.UserName == userName);

            // For each include, include in the query
            foreach (var include in includes)
                users = users.Include(include);

            // Return our result
            return await users.FirstOrDefaultAsync();
        }


        //This is called on initial login and is necessary to pull additional related user data
