string username = "user";
string password = "pass";

ContextOptions ctxOptions = ContextOptions.Negotiate | ContextOptions.Signing | ContextOptions.Sealing;
using (PrincipalContext context = new PrincipalContext(ContextType.Domain, domain, null, ctxOptions, username, password))
{
    using (var gp = GroupPrincipal.FindByIdentity(context, IdentityType.SamAccountName, "Group1"))
    {
        using (var searchResult = gp.GetMembers(recursive: false))
