{
       private readonly Queue<Action> _eventsQueue = new Queue<Action>();
       public void PostUnsafe(Action a){
             _eventsQueue.Enqueue(a);
       }

       //...

       public void DoEvents()
       { 
            while(_eventsQueue.TryDequeue(out var a)
            {
                  try
                  {
                        // 1) Action should be created (2 times GC allocation)
                        // 2) One Virtual-call
                        // 3) For async-await statemachines another overhead is required to pass-back result of action execution.
                        a();

                        // Finally Action-based event loops have ~60 Mops/sec maximal performance
