BitArray class needs optimization. The following implementation is 5 times faster than the implementation of netcore 2.2:

> BitArray took 5958 ms
CustomBitArray took 1243 ms


```c#
using System;
using System.Collections;
using System.Diagnostics;
using System.Runtime.CompilerServices;

namespace ConsoleAppBenchmarks
{
    public class CustomBitArray
    {
        public int Length { get; }

        public bool this[int index]
        {
            [MethodImpl(MethodImplOptions.AggressiveInlining)]
            get
            {
                if(index >= Length) throw new ArgumentOutOfRangeException(nameof(index));

                int idx = index >> BitsPerItemLog2;

                return (_array[idx] & (1 << (index - (idx << BitsPerItemLog2)))) != 0;
            }
            [MethodImpl(MethodImplOptions.AggressiveInlining)]
            set
            {
                if (index >= Length) throw new ArgumentOutOfRangeException(nameof(index));
                int idx = index >> BitsPerItemLog2;

                if (value)
                {
                    _array[idx] |= (1 << (index - (idx << BitsPerItemLog2)));
                }
                else
                {
                    _array[idx] &= (~(1 << (index - (idx << BitsPerItemLog2))));
                }
            }
        }
   

        public CustomBitArray(int length)
        {
            Length = length;
            _array = new int[(length >> BitsPerItemLog2) + 1];
        }

        private const int BitsPerItemLog2 = 5;

        private readonly int[] _array;
    }

    class Program
    {
        static void Main(string[] args)
        {
            const int iterations = 1000;
            const int length = 64 * 10000;

            Console.WriteLine("BitArray took {0} ms", TestBitArray(iterations, length));
            Console.WriteLine("CustomBitArray took {0} ms", TestCustomBitArray(iterations, length));
            Console.Read();
        }

        static long  TestBitArray(int iterations, int length)
        {
            var bitArray = new BitArray(length);
           Stopwatch sw = new Stopwatch();
           sw.Start();

           for (int j = 0; j < iterations; j++)
           {
               for (int i = 0; i < length;)
               {
                   bitArray[i] = true;
                   if (bitArray[i])
                   {
                       i++;
                   }
               }
            }
           sw.Stop();

           return sw.ElapsedMilliseconds;
        }

        static long TestCustomBitArray(int iterations, int length)
        {
            var bitArray = new CustomBitArray(length);

            Stopwatch sw = new Stopwatch();
            sw.Start();

            for (int j = 0; j < iterations; j++)
            {
                for (int i = 0; i < length;)
                {
                    bitArray[i] = true;
                    if (bitArray[i])
                    {
                        i++;
                    }
                }
            }
            sw.Stop();

            return sw.ElapsedMilliseconds;
        }



    }
}
```

