            }

            try
            {
                Console.WriteLine($">>> StructGeneric<int>'");
                var genericType = typeof(StructGeneric<>).MakeGenericType(typeof(int));
                Console.WriteLine($"<<< Got {genericType}");
            }
            catch (Exception e)
            {
                Console.WriteLine($"!!! Failed StructGeneric: {e}");
            }

            try
            {
                Console.WriteLine($">>> StructGeneric<MyEnum>'");
                var genericType = typeof(StructGeneric<>).MakeGenericType(typeof(MyEnum));
                Console.WriteLine($"<<< Got {genericType}");
            }
            catch (Exception e)
            {
                Console.WriteLine($"!!! Failed StructGeneric: {e}");
            }

            try
            {
                Console.WriteLine($">>> NewGeneric<MyEnum>'");
                var genericType = typeof(NewGeneric<>).MakeGenericType(typeof(MyEnum));
                Console.WriteLine($"<<< Got {genericType}");
            }
            catch (Exception e)
            {
                Console.WriteLine($"!!! Failed NewGeneric: {e}");
            }

            try
            {
                Console.WriteLine($">>> NotNullGeneric<MyEnum>'");
                var genericType = typeof(NotNullGeneric<>).MakeGenericType(typeof(MyEnum));
                Console.WriteLine($"<<< Got {genericType}");
            }
            catch (Exception e)
            {
                Console.WriteLine($"!!! Failed NotNullGeneric: {e}");
            }

            try
            {
                Console.WriteLine($">>> ComparableGeneric<MyEnum>'");
                var genericType = typeof(ComparableGeneric<>).MakeGenericType(typeof(MyEnum));
                Console.WriteLine($"<<< Got {genericType}");
            }
            catch (Exception e)
            {
                Console.WriteLine($"!!! Failed ComparableGeneric: {e}");
            }
        }
    }
}
