    internal sealed class MetadataEditList
    {
        private static readonly object OriginalRowMarker = new object();

        private readonly IndexedLookup<object>[] tables;
        private readonly MetadataReader reader;

        public MetadataEditList(MetadataReader reader)
        {
            var tableIndexes = (TableIndex[])Enum.GetValues(typeof(TableIndex));

            tables = new IndexedLookup<object>[tableIndexes.Max(i => (int)i) + 1];
            this.reader = reader;
        }

        public void Keep(EntityHandle handle) => Set(handle, OriginalRowMarker);

        public void Replace(CustomAttributeHandle handle, CustomAttribute newRow) => Set(handle, newRow);

        public CustomAttributeHandle Add(CustomAttribute newRow)
        {
            Set()
        }

        private IndexedLookup<object> GetTable(HandleKind handleKind)
        {
            if (!MetadataTokens.TryGetTableIndex(handleKind, out var tableIndex))
                throw new ArgumentException("The specified handle kind is not stored in a table.");

            return tables[(int)tableIndex];
        }

        private void Set(EntityHandle handle, object data)
        {
            if (handle.IsNil)
                throw new ArgumentException("The specified handle must not be nil.", nameof(handle));

            ref var element = ref GetTable(handle.Kind)[reader.GetRowNumber(handle)];

            if (element is object)
                throw new InvalidOperationException($"The specified handle has already been {(element == OriginalRowMarker ? "kept" : "replaced")}.");

            element = OriginalRowMarker;
        }
    }
}
