   Console.WriteLine("A=" + Thread.CurrentThread.ManagedThreadId);
   Task<IBuffer> readTask = serialDevice.InputStream.ReadAsync(response.AsBuffer(), RESPONSE_LENGTH, InputStreamOptions.None)
                                                    .AsTask(cancellationTokenSource.Token);
   await serialDevice.OutputStream.WriteAsync(request.AsBuffer());
