char[] input2;
uint arg;

[GlobalSetup(Target = nameof(MurmurHashBenchmark))]
public void setup2()
{
    int min = 0;
    int max = 100;
    Random randNum = new Random();
    arg = (uint)randNum.Next(min, max);

    input2 = new char[max];

    for (int i = 0; i < input2.Length; i++)
    {
        input2[i] = (char)randNum.Next(min, 128);
    }

}

[Benchmark]
public void MurmurHashBenchmark()
{
    ReadOnlySpan<char> _t = input2.AsSpan();
    MurmurHash(arg, _t);
}

public void MurmurHash(uint hash, ReadOnlySpan<char> span)
{
    ulong cur = 0;
    int bits = 0;
    for (int ich = 0; ich < span.Length; ich++)
    {
        uint ch = span[ich];
        cur |= ch << bits;
        bits += 8;

        if (bits >= 32)
        {
            hash = MurmurRound(hash, (uint)cur);
            cur = cur >> 32;
            bits -= 32;
        }
    }
}

public static uint MurmurRound(uint hash, uint chunk)
{
    chunk *= 0xCC9E2D51;
    chunk = Rotate(chunk, 15);
    chunk *= 0x1B873593;

    hash ^= chunk;
    hash = Rotate(hash, 13);
    hash *= 5;
    hash += 0xE6546B64;

    return hash;
}

private static uint Rotate(uint x, int r)
{
    return (x << r) | (x >> (32 - r));
}

```

