static bool IsEven(int x)
{
    if (x == 0 || x == 2 || x == 4 || x == 6)
        return true;

    IL.Push(x - 1);
    IL.Emit.Tail();
    IL.Emit.Call(new MethodRef(typeof(Program), nameof(IsOdd)));
    return IL.Return<bool>();
