```using System;
using System.Diagnostics;
using System.Linq;
using System.Net;
using System.Threading;
using System.Threading.Tasks;

namespace ConnectionReuseTest
{
    class Program
    {
        static void Main(string[] args)
        {
            var targetUrl = args.FirstOrDefault()
                ?? ((Func<string>)(() =>
                    {
                        Console.Write("Enter url: ");
                        return Console.ReadLine();
                    }))();


            Console.CursorVisible = false;

            var sw = Stopwatch.StartNew();

            int successCountNoKeepAlive = 0;
            int failCountNoKeepAlive = 0;
            int successCountKeepAlive = 0;
            int failCountKeepAlive = 0;
            object cursorLock = new object();

            Task Run(bool keepAlive, ref int successCount, ref int failCount)
            {
                using (var wc = new TestWebClient(keepAlive))
                {
                    while (true)
                    {
                        try
                        {
                            wc.DownloadData(targetUrl);
                            successCount++;
                        }
                        catch (Exception ex)
                        {
                            lock (cursorLock)
                            {
                                Console.SetCursorPosition(0, 7);
                                Console.WriteLine("Last error: " + ex);
                                Console.SetCursorPosition(0, 0);
                            }
                            failCount++;
                        }
                    }
                }
            }

            Task.Run(() => Run(true, ref successCountKeepAlive, ref failCountKeepAlive));
            Task.Run(() => Run(false, ref successCountNoKeepAlive, ref failCountNoKeepAlive));

            while (true)
            {
                lock (cursorLock)
                {
                    Console.SetCursorPosition(0, 2);
                    Console.WriteLine($"   KeepAlive - S: {successCountKeepAlive} F: {failCountKeepAlive} req/s: {(successCountKeepAlive + failCountKeepAlive) / sw.Elapsed.TotalSeconds:0.00}");
                    Console.WriteLine($"No KeepAlive - S: {successCountNoKeepAlive} F: {failCountNoKeepAlive} req/s: {(successCountNoKeepAlive + failCountNoKeepAlive) / sw.Elapsed.TotalSeconds:0.00}");
                }
                Thread.Sleep(100);
            }
        }
    }

    internal class TestWebClient : WebClient
    {
        private bool _keepAlive;

        public TestWebClient(bool keepAlive)
        {
            _keepAlive = keepAlive;
        }

        protected override WebRequest GetWebRequest(Uri uri)
        {
            var webRequest = base.GetWebRequest(uri);
            if (webRequest != null)
            {
                if (webRequest is HttpWebRequest httpWebRequest)
                {
                    httpWebRequest.KeepAlive = _keepAlive;
                    httpWebRequest.ReadWriteTimeout = httpWebRequest.Timeout = (int)TimeSpan.FromSeconds(30).TotalMilliseconds;
                }
            }
            return webRequest;
        }
    }
}```

