using BenchmarkDotNet.Attributes;
using BenchmarkDotNet.Running;
using System.Net;
using System.Threading;
using System.Threading.Tasks;

[MemoryDiagnoser]
public class Program
{
    public static void Main(string[] args) => BenchmarkSwitcher.FromTypes(new[] { typeof(Program) }).Run(args);
    
    private ManualResetEventSlim _mre = new ManualResetEventSlim();

    [Benchmark] public IPHostEntry GetHostEntry_Name() => Dns.GetHostEntry("bing.com");

    [Benchmark] public IPAddress[] GetHostAddresses_Name() => Dns.GetHostAddresses("bing.com");
    [Benchmark] public IPAddress[] GetHostAddresses_Address() => Dns.GetHostAddresses("13.107.21.200");

    [Benchmark] public Task GetHostEntryAsync_Name() => Dns.GetHostEntryAsync("bing.com");

    [Benchmark] public Task GetHostAddressesAsync_Name() => Dns.GetHostAddressesAsync("bing.com");
    [Benchmark] public Task GetHostAddressesAsync_Address() => Dns.GetHostAddressesAsync("13.107.21.200");

    [Benchmark]
    public void BeginEndGetHostEntry_Name()
    {
        _mre.Reset();
        Dns.BeginGetHostEntry("bing.com", iar =>
        {
            Dns.EndGetHostEntry(iar);
            ((ManualResetEventSlim)iar.AsyncState).Set();
        }, _mre);
        _mre.Wait();
    }

    [Benchmark]
    public void BeginEndGetHostAddresses_Name()
    {
        _mre.Reset();
        Dns.BeginGetHostAddresses("bing.com", iar =>
        {
            Dns.EndGetHostAddresses(iar);
            ((ManualResetEventSlim)iar.AsyncState).Set();
        }, _mre);
        _mre.Wait();
    }

    [Benchmark]
    public void BeginEndGetHostAddresses_Address()
