internal struct Context
{
    private readonly long P1Home;
    private readonly long P2Home;
    private readonly long P3Home;
    private readonly long P4Home;
    private readonly long P5Home;
    private readonly long P6Home;

    internal ContextFlags ContextFlags;

    private readonly int MxCsr;

    private readonly short SegCs;
    private readonly short SegDs;
    private readonly short SegEs;
    private readonly short SegFs;
    private readonly short SegGs;
    private readonly short SegSs;

    private readonly int EFlags;

    private readonly long Dr0;
    private readonly long Dr1;
    private readonly long Dr2;
    private readonly long Dr3;
    private readonly long Dr6;
    private readonly long Dr7;
    
    private readonly long Rax;

    internal long Rcx;

    private readonly long Rdx;
    private readonly long Rbx;

    internal long Rsp;

    private readonly long Rbp;
    private readonly long Rsi;
    private readonly long Rdi;

    private readonly long R8;
    private readonly long R9;
    private readonly long R10;
    private readonly long R11;
    private readonly long R12;
    private readonly long R13;
    private readonly long R14;
    private readonly long R15;

    internal long Rip;

    private readonly SaveFormat FltSave;

    [MarshalAs(UnmanagedType.ByValArray, SizeConst = 26)]
    private readonly M128A[] VectorRegister;

    private readonly long VectorControl;
    private readonly long DebugControl;

    private readonly long LastBranchToRip;
    private readonly long LastBranchFromRip;

    private readonly long LastExceptionToRip;
    private readonly long LastExceptionFromRip;
}
```

Where `ContextFlags` refers to the following enum

```csharp
internal enum ContextFlags
{
    Control = 0x100001,
    Integer = 0x100002
}
```

`SaveFormat` refers to the following struct

```csharp
[StructLayout(LayoutKind.Sequential, Pack = 16)]
internal struct SaveFormat
{
    private readonly short ControlWord;
    private readonly short StatusWord;

    private readonly byte TagWord;

    private readonly byte Reserved1;

    private readonly short ErrorOpcode;
    private readonly int ErrorOffset;
    private readonly short ErrorSelector;

    private readonly short Reserved2;

    private readonly int DataOffset;
    private readonly short DataSelector;

    private readonly short Reserved3;

