  try{
        await DoSomething();
  }catch(OperationCanceledException ex)
{
 /** ... Reached on CTRL+Z .... **/
}
finally{
       _iHostApplicationLifetime.StopApplication();
        await _hostLifetime.StopAsync(ct); // clean exit
  	Console.WriteLine("Completes as expected");    
