**ConsoleApp25**

```C#
using System;
using System.IO;
using System.Reflection;
using System.Runtime.Loader;

namespace ConsoleApp25
{
    public class Program
    {
        static void Main(string[] args)
        {
            var context = new AppLoadContext(@"..\..\..\..\ConsoleApp1\bin\Debug\netcoreapp3.0\ConsoleApp1.dll");
            var asm = context.LoadFromAssemblyName(new AssemblyName("ConsoleApp1"));

            var executeMethodInfo = asm.GetType("ConsoleApp1.Program").GetMethod("Execute", BindingFlags.Public | BindingFlags.Static);
            var execute = (Action)Delegate.CreateDelegate(typeof(Action), executeMethodInfo);

            execute();
        }
    }

    internal class AppLoadContext : AssemblyLoadContext
    {
        private readonly AssemblyDependencyResolver _resolver;

        public AppLoadContext(string path) :
            base(isCollectible: true)
        {
            _resolver = new AssemblyDependencyResolver(path);
        }

        protected override Assembly Load(AssemblyName assemblyName)
        {
            var path = _resolver.ResolveAssemblyToPath(assemblyName);
            if (path != null)
            {
                return LoadFromAssemblyPath(path);
            }

            return null;
        }

        protected override IntPtr LoadUnmanagedDll(string unmanagedDllName)
        {
            var path = _resolver.ResolveUnmanagedDllToPath(unmanagedDllName);
            if (path != null)
            {
                return LoadUnmanagedDllFromPath(path);
            }

            return IntPtr.Zero;
        }
    }
}
