    public class SSDDetector
    {
        private readonly string modelLocation;
        private readonly string labelsLocation;
        private readonly string imagesLocation;
        private readonly string tagsLocation;

        private IList<SSDBox> SSDBoxes = new List<SSDBox>();
        private readonly SSDPredictions ssdPredictions = new SSDPredictions();
        private readonly MLContext mLContext;

        public SSDDetector(string modelLocation,
                           string labelsLocation,
                           string imagesLocation,
                           string tagsLocation)
        {
            this.modelLocation = modelLocation;
            this.labelsLocation = labelsLocation;
            this.imagesLocation = imagesLocation;
            this.tagsLocation = tagsLocation;

            mLContext = new MLContext();
        }

        public struct ImageSettings
        {
            public const int ImageWidth = 1200;
            public const int ImageHeight = 1200;
        }

        public struct SSDSettings
        {
            public const string SSDInput = "image";
            public const string SSDBoxesOutput = "bboxes";
            public const string SSDLabelsOutput = "labels";
            public const string SSDScoresOutput = "scores";
        }

        public PredictionEngine<ImageData, ImagePredictions> LoadModel(string modelLocation,
                                                                       string imagesLocation,
                                                                       string tagsLocation)
        {
            IDataView data = mLContext.Data.LoadFromTextFile<ImageData>(path: tagsLocation,
                                                                        hasHeader: false);
            var pipeline = mLContext.Transforms.LoadImages(outputColumnName: "image",
                                                           imageFolder: imagesLocation,
                                                           inputColumnName: nameof(ImageData.ImagePath))
                   .Append(mLContext.Transforms.ResizeImages(outputColumnName: "image",
                                                             imageWidth: ImageSettings.ImageWidth,
                                                             imageHeight: ImageSettings.ImageHeight,
                                                             inputColumnName: "image"))
                   .Append(mLContext.Transforms.ExtractPixels(outputColumnName: "image"))
                   .Append(mLContext.Transforms.ApplyOnnxModel(modelFile: modelLocation,
                                                               outputColumnNames: new[] { SSDSettings.SSDBoxesOutput,
                                                                                          SSDSettings.SSDLabelsOutput,
                                                                                          SSDSettings.SSDScoresOutput },
                                                               inputColumnNames: new[] { SSDSettings.SSDInput }));

            var model = pipeline.Fit(data);

            var predictionEngine = mLContext.Model.CreatePredictionEngine<ImageData, ImagePredictions>(model);
            return predictionEngine;
        }
