        private PredictionEngine<ImageData, ImagePrediction> LoadModel(string dataLocation, string imagesLocation, string modelLocation)
        {
            var data = mLContext.Data.LoadFromTextFile<ImageData>(dataLocation, hasHeader: false);

            var pipeline = mLContext.Transforms.LoadImages(outputColumnName: "loadedimage", imageFolder: imagesLocation, inputColumnName: nameof(ImageData.ImagePath))
                .Append(mLContext.Transforms.ResizeImages(outputColumnName: "resizedimage",inputColumnName:"loadedimage", imageWidth: ImageSettings.imageWidth, imageHeight: ImageSettings.imageHeight))
                .Append(mLContext.Transforms.ExtractPixels(outputColumnName: "image", inputColumnName:"resizedimage", orderOfExtraction: ImagePixelExtractingEstimator.ColorsOrder.ABGR))
                .Append(mLContext.Transforms.ApplyOnnxModel(modelFile: modelLocation, outputColumnNames: new[] { "6379", "6381", "6383" }, inputColumnNames: new[] { "image"}));
            var model = pipeline.Fit(data);

