      var calibratorEstimator = mlContext.BinaryClassification.Calibrators.Platt();

      // Convert IDataView object to a list.
      var predictions = mlContext.Data.CreateEnumerable<Prediction>(transformedTestData, false).ToList();
      var calibratorTransformer = calibratorEstimator.Fit(transformedTestData);
      var finalData = calibratorTransformer.Transform(transformedTestData);
      var outScoresAndProbabilities = mlContext.Data.CreateEnumerable<ScoreAndProbabilityValue>(finalData, false);

      Console.WriteLine("\nFirst 10 acutals that are true");
      Console.WriteLine("Actual Predicted     score   probability");
      Console.WriteLine("------ --------- ----------  -----------");
      var loop = 1;
      for (var index = 0; index < predictions.Count(); index++) {
        var p1 = predictions.ElementAt(index);
        if (!p1.Label) continue;
        var p2 = outScoresAndProbabilities.ElementAt(index);
        Console.WriteLine("{0, 6} {1, 9} {2, 10}  {3, 11}", p1.Label, p1.PredictedLabel, p2.Score, p2.Probability);
        if (++loop > 10) break;
      }

      var metrics = mlContext.BinaryClassification.Evaluate(transformedTestData);
      PrintMetrics(metrics);
    }
