      // Create a clone copy of the current allLocations ref data
      var dataRef = v.currentLocationsData.allLocationsReference.slice()

      // The array reOrder function for re-sorting the array data after drag sort
      // so it matches the list after ui drag and drop
      function reOrderArray (arr, from, to) {
