export default new Router({
	routes: [
	  	{
			path: '/',
			redirect: '/login'
		},
		{
			path: '/login',
			name: 'login',
			component: Login,
		},
		{
			path: '/member',
			name: 'Member',
			component: Member,
			children:[
				{
					path: '/',
					redirect: '/member/coupon',
				},
				{
					path: '/member/coupon',
					name: 'Coupon',
					component: Coupon,
				},
				{
					path: '/member/info',
					name: 'MemberInfo',
					component: MemberInfo,
					children: [
						{
							path: '/member/info/i',
							name: 'MemberInfoI',
							component: MemberInfoI,
							children: []
						},
						{
							path: '/member/info/c',
							name: 'MemberInfoC',
							component: MemberInfoC,
							children: []
						}
					]
				}
			],
		}
  	]
})
