import Test from './Test.vue'
export default {
     components: {
        Test
    }
}
</script>
<style scoped>
.test {
    display: flex;
}

.test2 {
   width: 200px;
   height: 200px;
   background: black;
}
</style>
```

Test.vue
```
<template>
  <div>
    <div v-if="f" class="_test_style_class_name">
    </div>
    <slot v-else>
    </slot>
  </div>
</template>
<script>
export default {
     data () {
         return {
             f: true
         }     
     },
     mounted () {  this.f = false }
}
