              email: `${id}@email.com`
            })
          }, 1000)
        })
      }
      const store = new Vuex.Store({
        state: {
          users: {}
        },
        mutations: {
          addUser(state, user) {
            state.users[user.id] = user
          }
        },
        actions: {
          async getUser(ctx, id) {
            if (id in ctx.state.users) return
            ctx.commit('addUser', { id, email: '-' })
            const user = await getUserById(id)
            ctx.commit('addUser', user)
          }
        }
      })
      const app = new Vue({
        store,
        el: '#demo',
