	{
		name: 'Login',
		path: '/login',
		component: Login,
		props: { name: 'Авторизация' }
	},
	{
		name: 'Index',
		path: '/Index',
		component: Index,
		props: { name: 'Страницы' }
	}
];

export const router = new VueRouter({
	routes,
	mode: 'history'
});

```
