### Steps to reproduce
import { transformURL } from "./utils/file";
(function(imageDesc) {
  const imageNativeSet = imageDesc.set;
  Object.defineProperty(Image.prototype, "src", {
    async set(url) {
      url = await transformURL(url); // 将不能直接显示的URL转成base64
      imageNativeSet.call(this, url); // 重新设置IMAGE -> SRC = base64
