    <Table :columns="columns" :data="data"></Table>
</template>
<script>
export default {
  name: 'NestedTable',
  props: ['data'],
  data() {
    return {
      columns: [
        {
          type: "expand",
          width: 50,
          render: (h, params) => {
            let row = params.row;
            return (
               <NestedTable data={params.row.children}></NestedTable>
            );
          }
        }
      ]
    };
  }
};
}
</script>
