        // Using a DependencyProperty as the backing store for MyProperty.  This enables animation, styling, binding, etc...
        public static readonly DependencyProperty MyPropertyProperty =
            DependencyProperty.Register(&quot;MyProperty&quot;, typeof(int), typeof(MainWindow), new FrameworkPropertyMetadata(0,FrameworkPropertyMetadataOptions.Inherits, OnMyPropertyChanged));

